import "./FormPage.css";
import * as texts from "../../Utilities/textForApp";
import Button from "../../components/UI/Button/Button";
import { useState } from "react";
import UserDetailsForm from "./UserDetailsForm/UserDetailsForm";
import CheckBox from "../../components/UI/Input/CheckBox";

const FormPage = ({ref}) => {
  const [agreeIsChecked, setAgreeIsChecked] = useState(false);
  const [intrestedIsChecked, setIntrestedIsChecked] = useState(true);
  const [formUserDetails, setFormUserDetails] = useState();
  const [formIsValid, setFormIsValid] = useState(false);

  const onSubmit = (event) => {
    event.preventDefault();
    console.log(formUserDetails);
  };

  const checkBoxes = (
    <>
      <CheckBox
        name="agree"
        type="checkbox"
        label={texts.FORM_CHECK_BOX_1}
        value={agreeIsChecked}
        onCheckboxChange={() => {setAgreeIsChecked(!agreeIsChecked); checkFormValidity()}}
      />
      <CheckBox
        name="intrested"
        type="checkbox"
        label={texts.FORM_CHECK_BOX_2}
        value={intrestedIsChecked}
        onCheckboxChange={() => setIntrestedIsChecked(!intrestedIsChecked)}
      />
    </>
  );

  const getUserDetails = (userDetails) => {
    console.log("check");
    setFormUserDetails(userDetails);

    checkFormValidity();
  };

  /////asdfkajsdhfashdflkjhsdkfj
  const checkFormValidity = () => {
    let validationOfForm = true;
    for (let inputKey in formUserDetails) {
      validationOfForm =
        formUserDetails[inputKey].valid && validationOfForm && agreeIsChecked;
      console.log(validationOfForm);
    }
    setFormIsValid(validationOfForm);
  };

  return (
    <div className="formPage">
      <h2>{texts.FORM_H2}</h2>
      <form>
        <div className="textInputs">
          <UserDetailsForm userDetails={getUserDetails} />
        </div>
        <div className="checkBoxes">
          {checkBoxes}
          <Button
            text={texts.FORM_BTN}
            clicked={onSubmit}
            type="send"
            disable={!formIsValid}
          />
        </div>
      </form>
    </div>
  );
};

export default FormPage;
