import React from "react";

const Input = ({ name, type, value, changed, placeholder, options, label}) => {
  let inputElement = null;

  switch (type) {
    case "chekcbox":
      inputElement = (
        <label>
          <input
            type="checkbox"
            name={name}
            checked={value}
            onChange={changed}
          />
          {label}
        </label>
      );
      break;
    case "input":
      inputElement = (
        <input
          type={type}
          value={value}
          onChange={changed}
          placeholder={placeholder}
        />);
    case "select":
      inputElement = (
        <select
          value={value}
          onChange={changed}
        >
          {options.map((option) => (
            <option key={option.value} value={option.value}>
              {option.dispalyValue}
            </option>
          ))}
        </select>);
      break;
  }


  return (
    {inputElement}
  );
};

export default Input;


