import {FORM_AREA_CODE} from '../../../Utilities/textForApp';

const Input = ({  type, value, changed, placeholder, }) => {

  const options = ['050','052','054','055','056','058']
  let inputElement = null;

  switch (type) {
    case "input":
      inputElement = (
        <input
          type={type}
          value={value}
          onChange={changed}
          placeholder={placeholder}
        />
      );
    case "select":
      inputElement = (
        <select value={value} onChange={changed}>
          <option value="default" disabled hidden>
            {FORM_AREA_CODE}
          </option>
          {options.map((option) => (
            <option key={option} value={option}>
              {option}
            </option>
          ))}
        </select>
      );
      break;
  }

  return inputElement ;
};

export default Input;
