import { FORM_AREA_CODE } from "../../../Utilities/textForApp";
import classes from "./Input.module.css";

const Input = ({ type, value, changed, placeholder, invalid, touched }) => {
  const options = ["050", "052", "054", "055", "056", "058"];
  let inputElement = null;

  const inputClasses = [classes.InputElement];
  if (invalid && touched) {
    inputClasses.push(classes.Invalid);
  }
  const phoneClass = [classes.Phone]
  if (type === "phone" && invalid && touched) {
    inputClasses.push(classes.Phone);
  }

  switch (type) {
    case "text":
    case "email":
      inputElement = (
        <input
          type={type}
          value={value}
          onChange={changed}
          className={inputClasses.join(" ")}
          placeholder={placeholder}
        />
      );
      break;
    case "phone":
      inputElement = (
        <div className={classes.PhoneContainer}>
          <input
            type="number"
            value={value}
            onChange={changed}
            placeholder={placeholder}
            // className={inputClasses.join(" ") }
            className={classes.Phone}
          />
          <select
            defaultValue="default"
            onChange={changed}
            className={classes.AreaCode}
          >
            <option value="default" disabled hidden>
              {FORM_AREA_CODE}
            </option>
            {options.map((option) => (
              <option key={option} value={option}>
                {option}
              </option>
            ))}
          </select>
        </div>
      );
      break;
  }

  return inputElement;
};

export default Input;
