import MainPage from "./containers/mainPage/MainPage";
import VideoPage from "./containers/videoPage/VideoPage";
import FormPage from "./containers/formPAge/FormPage";
import "./App.css";
import { useRef } from "react";

function App() {
  const formPage = useRef();

  const executeScroll = () => formPage.current.scrollIntoView();
  // run this function from an event handler or an effect to execute scroll

  return (
    <div className="App">
      <header className="AppHeader"></header>
      <MainPage onScroll={executeScroll} />
      <VideoPage />
      <button onClick={executeScroll}>sdfsdf</button>
      <FormPage ref={formPage} />
      <footer className="app-footer"></footer>
    </div>
  );
}

export default App;
