import React from "react";

const Input = ({  type, value, changed, placeholder, }) => {
  let inputElement = null;

  switch (type) {

    case "input":
      inputElement = (
        <input
          type={type}
          value={value}
          onChange={changed}
          placeholder={placeholder}
        />
      );
    case "select":
      inputElement = (
        <select value={value} onChange={changed}>
          {options.map((option) => (
            <option key={option.value} value={option.value}>
              {option.dispalyValue}
            </option>
          ))}
        </select>
      );
      break;
  }

  return inputElement ;
};

export default Input;
