import "./FormPage.css";
import * as texts from "../../Utilities/textForApp";
import Button from "../../components/UI/Button/Button";
import { useState } from "react";
import UserDetailsForm from "./UserDetailsForm/UserDetailsForm";
import CheckBox from "../../components/UI/Input/CheckBox";

const FormPage = () => {
  const [agreeIsChecked, setAgreeIsChecked] = useState(false);
  const [intrestedIsChecked, setIntrestedIsChecked] = useState(true);

  const onSubmit = () => {
const requestOptions = {
  method: "POST",
  headers: { "Content-Type": "application/json" },
  body: JSON.stringify({ title: "React Hooks POST Request Example" }),
};
fetch("https://reqres.in/api/posts", requestOptions)
  .then((response) => response.json())
  .then((data) => setPostId(data.id));

  };

  const checkBoxes = (
    <>
      <CheckBox
        name="agree"
        type="checkbox"
        label={texts.FORM_CHECK_BOX_1}
        value={agreeIsChecked}
        changed={() => setAgreeIsChecked(!agreeIsChecked)}
      />
      <CheckBox
        name="intrested"
        type="checkbox"
        label={texts.FORM_CHECK_BOX_2}
        value={intrestedIsChecked}
        changed={() => setIntrestedIsChecked(!intrestedIsChecked)}
      />
    </>
  );


  return (
    <div className="formPage">
      <h2>{texts.FORM_H2}</h2>
      <form>
        <div className="textInputs">
          <UserDetailsForm/>
        </div>
        <div className="checkBoxes">
          {checkBoxes}
          <Button text={texts.FORM_BTN} clicked={onSubmit} type="send" />
        </div>
      </form>
    </div>
  );
};

export default FormPage;
