export const checkValidity = (value, rules) => {
    let isValid = true
    if (!rules) {
        return true;
    }
    if (rules.requird) {
        isValid = value.trim() !== '' && isValid && !value.includes(';');
    }
    if(rules.isName){
        const pattern = /^[א-ת '"״׳-]+$/;
        isValid = pattern.test(value) && isValid
    }
    if (rules.minLength) {
        isValid = value.length >= rules.minLength && isValid
    }
    if (rules.maxLength) {
        isValid = value.length <= rules.maxLength && isValid
    }
    if (rules.isEmail) {
        const pattern = /[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?/;
        isValid = pattern.test(value) && isValid
    }
    if (rules.isNumeric) {
        const pattern = /^\d+$/;
        isValid = pattern.test(value) && isValid
    }
    if (rules.isAddress) {
        const pattern = /^[א]+$/;
        isValid = pattern.test(value) && isValid
    }
    //add ruls for areaCode

    return isValid;
}
